function getCount(str) {
  let counter = 0;
  let vowels = 'aeiou';
  for (let i = 0; i < str.length; i++) {
    if (vowels.includes(str[i])) {
      counter++;
    }
  }
  return counter;
}

function filter_list(l) {
  return l.filter((n) => {
    return typeof n === 'number';
  });
}

function findShort(s){
  let arr = s.split(' ');
  let newArr = arr.map(word => word.length)
  return Math.min(...newArr);
}
